import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Chart from 'chart.js/auto';

function Dashboard() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const [dataSource, setDataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        expense: [],
        month: [],
      },
    ],
    labels: [],
  });

  const [selectedMonth, setSelectedMonth] = useState('');

  const createChart = () => {
    const ctx = document.getElementById('myChart').getContext('2d');

    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: dataSource.labels,
        datasets: [
          {
            label: 'Total Budget',
            data: dataSource.datasets[0].data,
            backgroundColor: 'rgba(0, 0, 139, 0.65)',
            borderWidth: 1,
          },
          {
            label: 'Expense',
            data: dataSource.datasets[0].expense,
            backgroundColor: 'rgba(255, 99, 132, 0.6)',
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            display: false,
          },
        },
      },
    });
  };

  const pieChart = () => {
    const ctx = document.getElementById('chart2').getContext('2d');
    const budgetForSelectedMonth = dataSource.datasets[0].data.find(
      (budget, index) => dataSource.datasets[0].month[index] === selectedMonth
    );

    new Chart(ctx, {
      type: 'pie',
      data: {
        labels: ['Budget', 'Remaining Budget'],
        datasets: [
          {
            data: [budgetForSelectedMonth, Math.max(0, budgetForSelectedMonth - calculateTotalExpense())],
            backgroundColor: ['rgba(0, 0, 139, 0.65)', 'rgba(255, 99, 132, 0.6)'],
            borderWidth: 1,
          },
        ],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        legend: {
          position: 'right',
        },
      },
    });
  };

  const calculateTotalExpense = () => {
    return dataSource.datasets[0].expense.reduce((total, expense) => total + expense, 0);
  };

  const getBudget = () => {
    axios
      .get(`http://localhost:3000/api/budget/${username}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        console.log(res.data);
        for (var i = 0; i < res.data.length; i++) {
          dataSource.datasets[0].data[i] = res.data[i].budget;
          dataSource.labels[i] = res.data[i].title;
          dataSource.datasets[0].backgroundColor[i] = res.data[i].color;
          dataSource.datasets[0].expense[i] = res.data[i].expense;
          dataSource.datasets[0].month[i] = res.data[i].month;
        }
        createChart();
        pieChart();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
  };

  useEffect(() => {
    getBudget();
  }, []);

  useEffect(() => {
    pieChart();
  }, [selectedMonth]);

  return (
    <div className="dashboard">
      <section className="dash-head">
        <h1 className="dashboard-head">Welcome to {username}'s Dashboard</h1>
        <div>
          <label htmlFor="monthDropdown">Select Month:</label>
          <select id="monthDropdown" value={selectedMonth} onChange={handleMonthChange}>
            <option value="">All Months</option>
            {Array.from(new Set(dataSource.datasets[0].month)).map((month) => (
              <option key={month} value={month}>
                {month}
              </option>
            ))}
          </select>
        </div>
        <Link to="/newcategory" className="create-category">
          Add new categories
        </Link>
        <Link to="/login" className="logout-link">
          Logout
        </Link>
      </section>
      <section className="charts">
        <article className="chart">
          <h3>Budget and Expenses based on Category</h3>
          <div>
            <canvas id="myChart"></canvas>
          </div>
        </article>
        <article className="chart">
          <h3>Budget Distribution for {selectedMonth}</h3>
          <div>
            <canvas id="chart2"></canvas>
          </div>
        </article>
      </section>
    </div>
  );
}

export default Dashboard;
