import React, { useState } from 'react';
import { DropdownButton, Dropdown } from 'react-bootstrap';

function TestC() {
  const [selectedMonth, setSelectedMonth] = useState('');

  const handleMonthChange = (eventKey, event) => {
    setSelectedMonth(monthIndexMap[eventKey]);
    console.log(selectedMonth);
  };

  const months = ['jan', 'may', 'oct'];

  // Create a mapping object for months to their indices
  const monthIndexMap = months.reduce((acc, month, index) => {
    acc[month] = index;
    return acc;
  }, {});

  return (
    <div className='testc'><DropdownButton id="monthDropdown" title={`Select Month: ${selectedMonth === '' ? 'All Months' : selectedMonth}`} onSelect={handleMonthChange}>
      <Dropdown.Item eventKey="">All Months</Dropdown.Item>
      {months.map((month) => (
        <Dropdown.Item key={month} eventKey={month}>
          {month}
        </Dropdown.Item>
      ))}
    </DropdownButton>
    </div>
  );
}

export default TestC;
