import React, { useEffect,useState } from 'react';
import axios from "axios"
import Chart from 'chart.js';
function HomePage() {

  const [dataSource, setDataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [
          '#ffcd56',
          '#ff6384',
          '#36a2eb',
          '#fd6b19',
        ],
      },
    ],
    labels: [],
  });

  const createChart = () => {
    const ctx = document.getElementById("myChart").getContext("2d");
    const myPieChart = new Chart(ctx, {
      type: 'pie',
      data: dataSource
    });
  }

  const getBudget = () => {
    axios.get('http://localhost:3000/budget')
      .then(function (res) {
        console.log(res);
        const newDataSource = { ...dataSource }; // Create a new object to avoid directly modifying state
        newDataSource.datasets[0].data = res.data.myBudget.map(item => item.budget);
        newDataSource.labels = res.data.myBudget.map(item => item.title);
        setDataSource(newDataSource); // Update state with the new data
        createChart();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  }

  useEffect(() => {
    getBudget();
  }, []);
  return (
    <main className="center" id="skiptomain">

        <div className="page-area">
            <article>
                <h1>Stay on track</h1>
                <p>
                    Do you know where you are spending your money? If you really stop to track it down,
                    you would get surprised! Proper budget management depends on real data... and this
                    app will help you with that!
                </p>
            </article>
    
            <article>
                <h1>Alerts</h1>
                <p>
                    What if your clothing budget ended? You will get an alert. The goal is to never go over the budget.
                </p>
            </article>
    
            <article>
                <h1>Results</h1>
                <p>
                    People who stick to a financial plan, budgeting every expense, get out of debt faster!
                    Also, they to live happier lives... since they expend without guilt or fear... 
                    because they know it is all good and accounted for.
                </p>
            </article>
    
            <article>
                <h1>Free</h1>
                <p>
                    This app is free!!! And you are the only one holding your data!
                </p>
            </article>
    
            <article>
                <h1>Stay on track</h1>
                <p>
                    Do you know where you are spending your money? If you really stop to track it down,
                    you would get surprised! Proper budget management depends on real data... and this
                    app will help you with that!
                </p>
            </article>
    
            <article>
                <h1>Alerts</h1>
                <p>
                    What if your clothing budget ended? You will get an alert. The goal is to never go over the budget.
                </p>
            </article>
    
            <article>
                <h1>D3JS Chart</h1>
                    <div id="newChart"></div>
            </article>
    
            <article>
                <h1>Chart</h1>
                <p>
                   <canvas id="myChart" width="400" height="400"> </canvas>
                </p>
            </article>

        </div>

    </main>
  );
}

export default HomePage;
