import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function SignupPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [name, setName] = useState('');
  const [isPopupOpen, setPopupOpen] = useState(false);
  const navigate = useNavigate();
  function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:3000/api/signup', { email, password,username,name })
      .then(res => {
        console.log(res.data);
        console.log( res.data.success)
        if (res && res.data && res.data.success) {
          setPopupOpen(true);

          // Close the popup after a certain duration (e.g., 3 seconds)
          setTimeout(() => {
            setPopupOpen(false);
          }, 3000);
          navigate('/login');
        }
      })
      .catch(err => console.error(err));
  }
  return (
   <div className='login'>
     <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'></link>
      <section>
        <form onSubmit={handleSubmit} >
          <h1>Signup</h1>
          <div className="input-box">
            <input type="text" placeholder='Name' onChange={e => setName(e.target.value)} required />&nbsp;
            <i className='bx bxs-user'></i>
          </div>
          <div className="input-box">
            <input type="text" placeholder='Email' onChange={e => setEmail(e.target.value)} required />&nbsp;
            <i className='bx bxs-envelope'></i>
          </div>
          <div className="input-box">
            <input type="text" placeholder='Username' onChange={e => setUsername(e.target.value)} required />&nbsp;
            <i className='bx bxs-user'></i>
          </div>
          <div className="input-box">
            <input type="password" placeholder='Password' onChange={e => setPassword(e.target.value)} required />&nbsp;
            <i className='bx bxs-lock-alt'></i>
          </div>
          <button id="login" type="submit" className="btn">Login</button>
          <div className="createacc">
            Already have an account?&nbsp;
            <Link itemProp="url" to="/login">Login Here</Link>
          </div>
        </form>
      </section>
      {isPopupOpen && (
        <div className="popup">
          <div className="popup-content">
            <p>Account has been created successfully!</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default SignupPage;
