import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Dashboard() {
  const [userBudget, setUserBudget] = useState(null);
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
  
    if (storedUsername) {
      console.log('Making API request to:', `http://localhost:3000/api/budget/${storedUsername}`);
      axios.get(`http://localhost:3000/api/budget/${storedUsername}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(res => {
        console.log('API Response:', res);
        if (res && res.data && res.data.success) {
          document.title = 'Dashboard';
          setUserBudget(res.data);
        }
      })
      .catch(error => {
        console.error('Error fetching user budget:', error);
        // Handle errors here, such as displaying an error message to the user
      });
    }
  }, [token]);
  

  return (
    <div className="dashboard">
      <section className="dash-head">
        <h1 className="dashboard-head">Welcome to {username}'s Dashboard</h1>
        <Link to="/login" className="logout-link">
          Logout
        </Link>
      </section>
      <section>
      </section>
    </div>
  );
}

export default Dashboard;
