import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Chart from 'chart.js/auto';

function Dashboard() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const [dataSource, setDataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        expense: [],
        month: [],
      },
    ],
    labels: [],
  });
  const [selectedMonth, setSelectedMonth] = useState('');

  const createChart = () => {
    const ctx = document.getElementById('myChart').getContext('2d');
    const myPieChart = new Chart(ctx, {
      type: 'pie',
      data: dataSource,
    });
  };

  const getBudget = () => {
    axios
      .get(`http://localhost:3000/api/budget/${username}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        console.log(res.data);
        const newData = {
          datasets: [
            {
              data: [],
              backgroundColor: [],
              expense: [],
              month: [],
            },
          ],
          labels: [],
        };
        for (var i = 0; i < res.data.length; i++) {
          newData.datasets[0].data[i] = res.data[i].budget;
          newData.labels[i] = res.data[i].title;
          newData.datasets[0].backgroundColor[i] = res.data[i].color;
          newData.datasets[0].expense[i] = res.data[i].expense;
          newData.datasets[0].month[i] = res.data[i].month;
        }
        setDataSource(newData);
        createChart();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  };

  const displayChart = () => {
    // Filter data based on the selected month
    const filteredData = dataSource.labels.map((label, index) => {
      if (dataSource.datasets[0].month[index] === selectedMonth) {
        return {
          label,
          data: dataSource.datasets[0].expense[index],
        };
      }
      return null;
    });

    // Remove null entries (entries not matching the selected month)
    const filteredChartData = filteredData.filter((data) => data !== null);

    const ctx = document.getElementById('myChart').getContext('2d');
    const myBarChart = new Chart(ctx, {
      type: 'bar',
      data: {
        labels: filteredChartData.map((data) => data.label),
        datasets: [
          {
            label: 'Expense',
            data: filteredChartData.map((data) => data.data),
            backgroundColor: filteredChartData.map(() => 'rgba(75, 192, 192, 0.2)'),
            borderColor: filteredChartData.map(() => 'rgba(75, 192, 192, 1)'),
            borderWidth: 1,
          },
        ],
      },
    });
  };

  useEffect(() => {
    getBudget();
  }, []);

  return (
    <div className="dashboard">
      <section className="dash-head">
        <h1 className="dashboard-head">Welcome to {username}'s Dashboard</h1>
        <Link to="/newcategory" className="create-category">
          Add new categories
        </Link>
        <Link to="/login" className="logout-link">
          Logout
        </Link>
      </section>
      <article>
        <h1>Chart</h1>
        <p>
          <canvas id="myChart" width="400" height="400"></canvas>
        </p>
        <label htmlFor="month">Select Month:</label>
        <select
          id="month"
          name="month"
          onChange={(e) => {
            setSelectedMonth(e.target.value);
            displayChart();
          }}
        >
          <option value="">All Months</option>
          <option value="January">January</option>
          <option value="February">February</option>
          {/* Add other months as options */}
        </select>
      </article>
    </div>
  );
}

export default Dashboard;
