import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // Add this line to import Axios

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const login = () => {
    // Log the email and password to the console
    console.log('Email:', email);
    console.log('Password:', password);

    // Your login logic here
    // const isValid = false; // Set your condition for validity

    // if (isValid) {
    //   // Navigate to "/about" if the condition is satisfied
    //   navigate('/about');
    // }

    axios.post('/api/login', { email, password })
      .then(res => {
        console.log(res);
        document.getElementById('email').value = '';
        document.getElementById('password').value = '';
        if (res && res.data && res.data.success) {
          // const token = res.data.token;
          // localStorage.setItem('jwt', token);
          // history.pushState({}, "", "/about");
          // navigate('/about');
        }
      });
  };

  return (
    <div className='login'>
      <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'></link>
      <section>
        <form>
          <h1>Login</h1>
          <div className="input-box">
            <input type="text" name="email" id="email" placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} required />&nbsp;
            <i className='bx bxs-envelope'></i>
          </div>
          <div className="input-box">
            <input type="password" name="password" id="password" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} required />&nbsp;
            <i className='bx bxs-lock-alt'></i>
          </div>
          {/* <div className="ForgotPass">
            <Link itemProp="url" to="/signup">Forgot Password?</Link>
          </div> */}
          <button id="login" onClick={login} type="submit" className="btn">Login</button>
          <div className="createacc">
            Don't have an account?&nbsp;
            <Link itemProp="url" to="/signup">Register Here</Link>
          </div>
        </form>
      </section>
    </div>
  );
}

export default LoginPage;
