import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import Axios

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const login = () => {
    // Make an HTTP request to your server's /login endpoint
    axios.post('/api/login', { email, password })
      .then((res) => {
        console.log(res.data); // Log the response for debugging
        if (res.data.success) {
          // If the login is successful, navigate to "/about"
          navigate('/about');
        } else {
          // Handle unsuccessful login (e.g., show an error message)
          console.error('Login failed:', res.data.err);
        }
      })
      .catch((error) => {
        // Handle any errors that occurred during the request
        console.error('Error during login request:', error);
      });
  };

  return (
    <div className='login'>
      <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'></link>
      <section>
        <form>
          <h1>Login</h1>
          <div className="input-box">
            <input type="text" placeholder='Email' value={email} onChange={(e) => setEmail(e.target.value)} required />&nbsp;
            <i className='bx bxs-envelope'></i>
          </div>
          <div className="input-box">
            <input type="password" placeholder='Password' value={password} onChange={(e) => setPassword(e.target.value)} required />&nbsp;
            <i className='bx bxs-lock-alt'></i>
          </div>
          <button id="login" type="button" className="btn" onClick={login}>
            Login
          </button>
          <div className="createacc">
            Don't have an account?&nbsp;
            <Link itemProp="url" to="/signup">Register Here</Link>
          </div>
        </form>
      </section>
    </div>
  );
}

export default LoginPage;
