// Proj.js
import React, { useState, useEffect } from 'react';
import './Proj.scss';

import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate
} from 'react-router-dom';

import Header from './Header/Header';
import Footer from './Footer/Footer';
import Dashboard from './Dashboard/Dashboard';
import LoginPage from './LoginPage/LoginPage';
import StartPage from './StartPage/StartPage';
import SignupPage from './SignupPage/SignupPage';
import TestC from './TestC/TestC';
import NewCategory from './NewCategory/NewCategory';

function Proj() {
  const [authenticated, setAuthenticated] = useState(false);

  // Check if the user is already authenticated (e.g., token in localStorage)
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      setAuthenticated(true);
    }
  }, []);

  const PrivateRoute = ({ element, ...rest }) => {
    return authenticated ? (
      element
    ) : (
      <Navigate to="/login" replace />
    );
  };

  return (
    <Router>
      <div className="mainContainer">
        <Header />
        <Routes>
          <Route path="/" element={<StartPage />} />
          <Route path="/login" element={<LoginPage setAuthenticated={setAuthenticated} />} />
          <Route path="/signup" element={<SignupPage />} />
          <Route path="/dashboard" element={<PrivateRoute element={<Dashboard />} />} />
          <Route path="/newcategory" element={<PrivateRoute element={<NewCategory />} />} />
          <Route path="/testc" element={<PrivateRoute element={<TestC />} />} />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
}

export default Proj;
