import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Chart from 'chart.js/auto';
import * as d3 from 'd3';


function Dashboard() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const [dataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [
          '#ffcd56',
          '#ff6384',
          '#36a2eb',
          '#fd6b19',
          '#9BA960',
          '#964B00',
          '#581845'
        ],
      },
    ],
    labels: [],
  });

  const createChart = () => {
    const ctx = document.getElementById("myChart").getContext("2d");
    const myPieChart = new Chart(ctx, {
      type: 'pie',
      data: dataSource
    });
  }
  const newChart = () =>{
    const width = 400,
                height = 400,
                margin = 40;
            const radius = Math.min(width, height) / 2 - margin;
            const svg = d3.select("#newChart")
                .append("svg")
                .attr("width", width)
                .attr("height", height)
                .append("g")
                .attr("transform", `translate(${width / 2},${height / 2})`);

            const color = d3.scaleOrdinal()
                .domain(dataSource.labels) 
                .range(d3.schemeDark2);

            const pie = d3.pie()
                .sort(null) 
                .value(d => d.data);

            const data_ready = pie(dataSource.datasets[0].data.map((data, index) => ({
                data: data,
                label: dataSource.labels[index]
            })));

            const arc = d3.arc()
                .innerRadius(radius * 0.5) 
                .outerRadius(radius * 0.8);
            const outerArc = d3.arc()
                .innerRadius(radius * 0.9)
                .outerRadius(radius * 0.9);

            svg
                .selectAll('allSlices')
                .data(data_ready)
                .join('path')
                .attr('d', arc)
                .attr('fill', d => color(d.data.label)) 
                .attr("stroke", "white")
                .style("stroke-width", "2px")
                .style("opacity", 0.7);

            svg
                .selectAll('allPolylines')
                .data(data_ready)
                .join('polyline')
                .attr("stroke", "black")
                .style("fill", "none")
                .attr("stroke-width", 1)
                .attr('points', function (d) {
                    const posA = arc.centroid(d); 
                    const posB = outerArc.centroid(d); 
                    const posC = outerArc.centroid(d); 
                    const midangle = d.startAngle + (d.endAngle - d.startAngle) / 2; 
                    posC[0] = radius * 0.95 * (midangle < Math.PI ? 1 : -1); 
                    return [posA, posB, posC];
                });

            svg
                .selectAll('allLabels')
                .data(data_ready)
                .join('text')
                .text(d => d.data.label) 
                .attr('transform', function (d) {
                    const pos = outerArc.centroid(d);
                    const midangle = d.startAngle + (d.endAngle - d.startAngle) / 2;
                    pos[0] = radius * 0.99 * (midangle < Math.PI ? 1 : -1);
                    return `translate(${pos})`;
                })
                .style('text-anchor', function (d) {
                    const midangle = d.startAngle + (d.endAngle - d.startAngle) / 2;
                    return (midangle < Math.PI ? 'start' : 'end');
                });
  }

  const getBudget = () => {
    axios.get(`http://localhost:3000/api/budget/${username}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }).then(function (res) {
       // console.log(res);
        for (var i = 0; i < res.data.myBudget.length; i++) {
          dataSource.datasets[0].data[i] = res.data.myBudget[i].budget;
          dataSource.labels[i] = res.data.myBudget[i].title;
      }
      createChart();
      newChart();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  }

  useEffect(() => {
    getBudget();
  }, []);

  return (
    <div className="dashboard">
      <section className="dash-head">
        <h1 className="dashboard-head">Welcome to {username}'s Dashboard</h1>
        <Link to="/login" className="logout-link">
          Logout
        </Link>
      </section>
      <article>
                <h1>Chart</h1>
                <p>
                   <canvas id="myChart" width="400" height="400"> </canvas>
                </p>
            </article>

      <article>
                <h1>D3JS Chart</h1>
                    <div id="newChart"></div>
            </article>
    </div>
  );
}

export default Dashboard;
