import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Chart from 'chart.js/auto';
import * as d3 from 'd3';


function Dashboard() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const [dataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        expense:[],
        month:[]
      },
    ],
    labels: [],
  });
  const createChart = (chartId, data, backgroundColor, label, borderColor) => {
    const ctx = document.getElementById(chartId).getContext('2d');

    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: data.labels,
        datasets: [
          {
            label,
            data: data.datasets[0][chartId],
            backgroundColor,
            borderColor,
            borderWidth: 1,
          },
        ],
      },
      options: {
        scales: {
          y: {
            display: false,
          },
        },
      },
    });
  };

  const getBudget = () => {
    axios.get(`http://localhost:3000/api/budget/${username}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }).then(function (res) {
        console.log(res.data);
        for (var i = 0; i < res.data.length; i++) {
          dataSource.datasets[0].data[i] = res.data[i].budget;
          dataSource.labels[i] = res.data[i].title;
          dataSource.datasets[0].backgroundColor[i] = res.data[i].color ;
          dataSource.datasets[0].expense[i] = res.data[i].expense;
          dataSource.datasets[0].month[i] = res.data[i].month;
        }
        createChart();
        // newChart();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  }
  
  useEffect(() => {
    getBudget();
  }, []);
  
  return (
    <div className="dashboard">
      <section className="dash-head">
        <h1 className="dashboard-head">Welcome to {username}'s Dashboard</h1>
        <Link to="/newcategory" className="create-category">
          Add new categories
        </Link>
        <Link to="/login" className="logout-link">
          Logout
        </Link>
      </section>
      <article className='chart1'>
       <h4>Budget and Expenses based on Category</h4>
        <div >
          <canvas id="myChart"> </canvas>
        </div>
      </article>
      <article>
                <h1>D3JS Chart</h1>
                    <div id="newChart"></div>
            </article> 
    </div>
  );
}

export default Dashboard;
