  import React, { useState, useEffect } from 'react';
  import { Link } from 'react-router-dom';
  import axios from 'axios';
  import Chart from 'chart.js/auto';

  function Dashboard() {
    const username = localStorage.getItem('username');
    const token = localStorage.getItem('jwt');
    const months = [
      'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec',
    ];

    const options = months.map((month) => ({
      label: month,
      value: month.toLowerCase(), // using lowercase for consistency
    }));

    const [selectedMonth, setSelectedMonth] = React.useState('');

    const handleChange = (event) => {
      setSelectedMonth(event.target.value);
    };
    const [dataSource, setDataSource] = useState({
      datasets: [
        {
          data: [],
          backgroundColor: [],
          expense: [],
          month: [],
        },
      ],
      labels: [],
    });
    const createChart = () => {
      const ctx = document.getElementById('myChart').getContext('2d');

      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: dataSource.labels,
          datasets: [
            {
              label: 'Total Budget',
              data: dataSource.datasets[0].data,
              backgroundColor: 'rgba(0, 0, 139, 0.65)',
              borderWidth: 1,
            },
            {
              label: 'Expense',
              data: dataSource.datasets[0].expense,
              backgroundColor: 'rgba(255, 99, 132, 0.6)',
              borderWidth: 1,
            },
          ],
        },
        options: {
          scales: {
            y: {
              display: false,
            },
          },
        },
      });
    };

    const createPieChart = () => {
      const ctx = document.getElementById('chart2').getContext('2d');
    
      // Check if a chart instance already exists and destroy it
      const existingChart = Chart.getChart(ctx);
      if (existingChart) {
        existingChart.destroy();
      }
    
      const filteredData = dataSource.datasets[0].expense.filter(
        (expense, index) => dataSource.datasets[0].month[index] === selectedMonth
      );
      const filteredLabels = dataSource.labels.filter(
        (_, index) => dataSource.datasets[0].month[index] === selectedMonth
      );
      const filteredColors = dataSource.datasets[0].backgroundColor.filter(
        (_, index) => dataSource.datasets[0].month[index] === selectedMonth
      );
    
      if (filteredData.length === 0) {
        // If no expenses for the selected month, display a message
        ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
        ctx.font = '20px Arial';
        ctx.textAlign = 'center';
        ctx.fillText('No expenses this month', ctx.canvas.width / 2, ctx.canvas.height / 2);
      } else {
        // If there are expenses, create the pie chart
        new Chart(ctx, {
          type: 'pie',
          data: {
            labels: filteredLabels,
            datasets: [
              {
                data: filteredData,
                backgroundColor: filteredColors, // Use colors from the database
                borderWidth: 1,
              },
            ],
          },
          options: {
            responsive: true,
            maintainAspectRatio: false,
            legend: {
              position: 'right',
            },
          },
        });
      }
    };
    
    // const
    // const allmonths = [
    //   'January', 'February', 'March', 'April', 'May', 'June',
    //   'July', 'August', 'September', 'October', 'November', 'December'
    // ];
    // const currentMonth = allmonths[today.getMonth()];
    
    // console.log(currentMonth);
    
    const createStackedLineChart = () => {
      const ctx = document.getElementById('chart3').getContext('2d');
    
      // Calculate amount difference
      const amountDifference = dataSource.datasets[0].data.map((budget, index) => {
        const expenses = dataSource.datasets[0].expense[index];
        return budget - expenses;
      });
    
      new Chart(ctx, {
        type: 'line',
        data: {
          labels: dataSource.labels,
          datasets: [
            {
              label: 'Budget',
              data: dataSource.datasets[0].data,
              borderColor: 'rgba(0, 255, 0, 0.8)',
              backgroundColor: 'rgba(0, 255, 0, 0.2)',
              borderWidth: 2,
              fill: 'origin',
            },
            {
              label: 'Expenses',
              data: dataSource.datasets[0].expense,
              borderColor: 'rgba(255, 0, 0, 0.8)',
              backgroundColor: 'rgba(255, 0, 0, 0.2)',
              borderWidth: 2,
              fill: '-1',
            },
            {
              label: 'Amount Difference',
              data: amountDifference,
              borderColor: 'rgba(0, 0, 255, 0.8)',
              backgroundColor: 'rgba(0, 0, 255, 0.2)',
              borderWidth: 2,
              fill: '-1',
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            x: {
              display: true,
              title: {
                display: true,
                text: 'Months',
              },
            },
            y: {
              display: true,
              title: {
                display: true,
                text: 'Amount',
              },
            },
          },
        },
      });
    };
    
    useEffect(() => {
      createPieChart(); // Call the pie chart creation function
    }, [selectedMonth, dataSource]);

    const getBudget = () => {
      axios
        .get(`http://localhost:3000/api/budget/${username}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        })
        .then(function (res) {
          console.log(res.data);
          for (var i = 0; i < res.data.length; i++) {
            dataSource.datasets[0].data[i] = res.data[i].budget;
            dataSource.labels[i] = res.data[i].title;
            dataSource.datasets[0].backgroundColor[i] = res.data[i].color;
            dataSource.datasets[0].expense[i] = res.data[i].expense;
            dataSource.datasets[0].month[i] = res.data[i].month;
          }
          createChart();
          // createPieChart(); // Call the pie chart creation function
          createStackedLineChart();
        })
        .catch(function (error) {
          console.error('Error fetching budget data:', error);
        });
    };

    useEffect(() => {
      getBudget();
    }, []);

    return (
      <div className="dashboard">
        <section className="dash-head">
          <h1 className="dashboard-head">Welcome to <b>{username}'s </b>Dashboard</h1>
          <Link to="/newcategory" className="create-category">
            Add new categories
          </Link>
          <Link to="/login" className="logout-link">
            Logout
          </Link>
        </section>
        <section className="charts">
          <article className="chart">
            <h3>Budget and Expenses based on Category</h3>
            <div>
              <canvas id="myChart"> </canvas>
            </div>
          </article>
          <article className="chart">
            <h3>All the expenses for <b>{selectedMonth}</b> </h3>
            <div>
              <label>
                <select value={selectedMonth} onChange={handleChange}>
                  <option value={''} disabled hidden>
                    Select a month
                  </option>
                  {options.map((option) => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </label>
              <div>
                <canvas id="chart2"> </canvas>
              </div>
            </div>
          </article>
          <div className="chart">
            <h3>Line chart</h3>
            <div>
              <canvas id="chart3"> </canvas>
            </div>
          </div>
        </section>
      </div>
    );
  }

  export default Dashboard;
