import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
function LoginPage() {
  const [click, setClick] = useState(0);

  const passwordEncrption = async (password) => {
    const saltRounds = 10;
    const hashedPassword = await bcrypt.hash(password, saltRounds);
    return hashedPassword;
  };

  const changeDateFormat = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const login = () => {
    const data = {
      username: document.getElementById('username').value,
      password: document.getElementById('password').value,
    };

    if (click === 0) {
      axios.post('/api/login', data)
        .then((res) => {
          console.log(res);
          document.getElementById('username').value = '';
          document.getElementById('password').value = '';
          if (res && res.data && res.data.success) {
            const token = res.data.token;
            localStorage.setItem('jwt', token);
            // Redirect to dashboard or handle as needed
          }
        })
        .catch((error) => {
          console.error('Error during login:', error);
        });
    } else {
      // Redirect to dashboard or handle as needed
    }
  };
  return (
   <div className='login'>
     <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'></link>
      <section>
        <form >
          <h1>Login</h1>
          <div className="input-box">
            <input type="text" placeholder='Email' required />&nbsp;
            <i class='bx bxs-envelope'></i>
          </div>
          <div className="input-box">
            <input type="password" placeholder='Password' required />&nbsp;
            <i className='bx bxs-lock-alt'></i>
          </div>
{/*           
          <div className="ForgotPass">
            <Link itemProp="url" to="/signup">Forgot Password?</Link>
          </div> */}
          <button id="login" onclick="login()" type="submit" class="btn">Login</button>
          <div className="createacc">
            Don't have an account?&nbsp;
            <Link itemProp="url" to="/signup">Register Here</Link>
          </div>
        </form>
      </section>
      
    </div>
    
  );
}

export default LoginPage;
