import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function NewCategory() {
  const [title, setTitle] = useState('');
  const [budget, setBudget] = useState('');
  const [color, setColor] = useState('');
  const [expense, setExpense] = useState('');
  const [isPopupOpen, setPopupOpen] = useState(false);
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');

  function handleSubmit(event) {
    event.preventDefault();
    console.log(title, budget, color, expense);

    axios.post('http://localhost:3000/addbudget',
        {
          title,
          budget,
          color,
          expense,
          username,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        console.log(res.data);
        console.log(res.data.success);
        if (res && res.data && res.data.success) {
          // Show the popup
          setPopupOpen(true);

          // Close the popup after a certain duration (e.g., 3 seconds)
          setTimeout(() => {
            setPopupOpen(false);
          }, 3000);
        }
      })
      .catch((err) => console.error(err));
  }

  return (
    <div className="new-category">
      <section className="newcat">
      <button className="back-to-dashboard" onClick={handleBackToDashboard}>
        Back to Dashboard
      </button>
        <form onSubmit={handleSubmit}>
          <h1>Add New Categories</h1>
          {/* title--labels
          budget--data
          color--bgcolor
          expense--expense
          username
          month--month */}
          <div className="input-box">
            <p>Title</p>
            <input
              type="text"
              placeholder="Enter Title"
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <p>Total Budget</p>
            <input
              type="text"
              placeholder="Enter Total Budget"
              onChange={(e) => setBudget(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <p>Color</p>
            <input
              type="text"
              placeholder="Enter Color"
              onChange={(e) => setColor(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <p>Expense</p>
            <input
              type="text"
              placeholder="Enter Expense"
              onChange={(e) => setExpense(e.target.value)}
              required
            />
          </div>
          <button id="add" type="submit" className="btn">
            Add
          </button>
        </form>
      </section>

      {/* Popup */}
      {isPopupOpen && (
        <div className="popup">
          <div className="popup-content">
            <p>Data has been created successfully!</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default NewCategory;
